        if grid[-1][-1] == '(':
            return False
        if (m + n) % 2 == 0:
            return False

        halfLen = (m + n) // 2

        helper = [[set([]) for _ in range(n)] for _ in range(m)]
        helper[0][0].add(1)

        for c in range(1, n):
            for e in helper[0][c-1]:
                if halfLen >= e + (1 if grid[0][c] == '(' else -1) >= 0:
                    helper[0][c].add(  e + (1 if grid[0][c] == '(' else -1) )

        for r in range(1, m):
            lst = [e + ( 1 if grid[r][0] == '(' else -1 ) for e in helper[r-1][0] if halfLen >= e + (1 if grid[r][0] == '(' else -1) >= 0]
