class Solution {
public:
    bool dp[101][101][101]; 
    // dp[i][j][k] stores the ans at position i,j where the difference between the number of left and the right brackets is k
    int vis[101][101][101];
    // vis[i][j][k] stores of we ever visit the position i, j where the difference between the number of left and the right brackets is k
    int n, m;
    bool find(int x, int y, vector<vector<char>>&grid, int l, int r){
        if(r > l) return false; // for optimizatin if right brackets if > left bracketthen obviously we can't reach to an ans eg:- ()) 
        if(l > (n+m-1)/2) return false; // in our final ans l will be equals to (n+m-1)/2 in case it is more than our required value we are on the wrong path
        if(vis[x][y][l-r] != -1) return dp[x][y][l-r];  // using memorization
        vis[x][y][l-r] = 1; // mark this position as visited
        if(x == n-1 && y == m-1){   // if we reach at bottom right
            return dp[x][y][l-r] = (l == r);    // return if l==r or not
        }
        bool ans = false; 
        if(x != n-1){ // if we can go down then try going down
            ans |= find(x+1, y, grid, l + (grid[x+1][y] == '('), r + (grid[x+1][y] == ')'));
        }
        if(ans) return dp[x][y][l-r] = ans; // if we find the ans return true no need to go to the right side now
        if(y != m-1){
            // in case we do not find the ans go right
            ans |= find(x, y+1, grid, l + (grid[x][y+1] == '('), r + (grid[x][y+1] == ')'));
        }
        return dp[x][y][l-r] = ans; //return the ans

    }

    bool hasValidPath(vector<vector<char>>& grid) {
        if(grid[0][0] == ')') return false; // if 1st grid is right bracket then return false;
        n = grid.size();
        m = grid[0].size();
        if((n + m - 1)&1) return false; // if the path contains odd number of grid return false
        memset(dp, false, sizeof(dp));
        memset(vis, -1, sizeof(vis));
        return find(0, 0, grid, 1, 0);  // return the ans
    }
};
