    boolean dfs(char[][] g, int r, int c, int v) {
        if (r == g.length-1 && c == g[0].length-1) {
            if (g[r][c] == '(') v++;
            else v--;
            if (v == 0) return true;
            else return false;
        } 
        if (r < 0 || c < 0 || r>g.length-1 || c > g[0].length-1) return false;

        String key = (r * g.length + c) + "_" + v;
        if (mem.containsKey(key)) return mem.get(key);
        boolean ans = false;
        if (g[r][c] == '(') v++;
        else {
            v--;
            if (v < 0) {
                mem.put(key, false);
                return false;
            }
        }
        ans = ans || dfs(g, r+1,c,v);
        ans = ans || dfs(g, r, c+1, v);

        mem.put(key, ans);
        return ans;
    }
}
