class Solution {
    private int R;
    private int C;
    private int[][][] dp;

    public boolean hasValidPath(char[][] grid) {

        this.R = grid.length;
        this.C = grid[0].length;
        this.dp = new int[R+1][C+1][201];

        for(int[][] i : dp){
            for(int[] j : i){
                Arrays.fill(j, -1);
            }
        }

        if(find(0, 0, 0, 0, grid)) return true;

        return false;
    }

    private boolean find(int r, int c, int left, int right, char[][] grid){
        if(r < 0 || r >= R ||c < 0 || c >= C){
            return false;
        }

        if(grid[r][c] == '('){
            left++;
        }else right++;

        if(left == right && r == R-1 && c == C-1){
            return true;
