class Solution {
private: 
    unordered_map<int, unordered_map<int, unordered_map<int, int>>> dp;
    int r, c;
    bool valid(int x, int y, int dif, vector<vector<char>> &grid) {
        if (x < 0 || y < 0 || x >= r || y >= c) return false;
        if (grid[x][y] == '(') dif++;
        else dif--;
        if (dif < 0 || dif > (r + c) / 2)    return false;
        if (x == r - 1 && y == c - 1 && !dif)   return true;
        if (dp[x][y].find(dif) != dp[x][y].end())    return dp[x][y][dif];
        return dp[x][y][dif] = valid(x, y + 1, dif, grid) || valid(x + 1, y, dif, grid);

    }
public:
    bool hasValidPath(vector<vector<char>>& grid) {
        r = grid.size(), c = grid[0].size();
        return valid(0, 0, 0, grid);
    }
};
