class Solution {

    int n, m, memo[102][102][205];

    int dp(vector<vector<char>>& grid, int i, int j, int sum) {
        if (i == n - 1 and j == m - 1) // Reach the final position
			return (sum == 0); 
        if (sum < 0) return false;
        if (memo[i][j][sum] != -1) return memo[i][j][sum];

        if (i == n - 1 and j != m - 1) // Last row
            return memo[i][j][sum] = dp(grid, i, j + 1, sum + (grid[i][j + 1] == '('? 1 : -1));
        if (i != n - 1 and j == m - 1) // Lats column
            return memo[i][j][sum] = dp(grid, i + 1, j, sum + (grid[i + 1][j] == '(' ? 1 : -1));

        return memo[i][j][sum] = dp(grid, i, j + 1, sum + (grid[i][j + 1] == '('? 1 : -1)) or dp(grid, i + 1, j, sum + (grid[i + 1][j] == '(' ? 1 : -1));
    }

public:
    bool hasValidPath(vector<vector<char>>& grid) {        
        n = grid.size();
        m = grid[0].size();
        memset(memo, -1, sizeof(memo));
        return dp(grid, 0, 0, grid[0][0] == '(' ? 1 : -1);
    }
};

