/**
 * @param {character[][]} grid
 * @return {boolean}
 */
const hasValidPath = function (grid) {
  const memo = {}
  const dfs = (row, col, diff) => {
    const key = `${row}-${col}-${diff}`
    if (memo[key] !== undefined) {
      return memo[key]
    }
    if (row < 0 || row >= grid.length || col < 0 || col >= grid[row].length) {
      return false
    }
    if (grid[row][col] === '(') {
      diff += 1
    } else {
      diff -= 1
    }
    if (diff < 0) {
      return false
    }
    if (row === grid.length - 1 && col === grid[row].length - 1 && diff === 0) {
      return true
    }
    let result = false

    result = result || dfs(row + 1, col, diff)
    result = result || dfs(row, col + 1, diff)
    memo[key] = result
    return result
  }
  return dfs(0, 0, 0, 0)
}

