class Solution {
    int counter = 0;
    Boolean[][][] dp = new Boolean[101][101][201];
    public boolean hasValidPath(char[][] grid) {
        int row = grid.length-1;
        int col = grid[0].length-1;
        if(grid[0][0] !='(' || grid[row][col] != ')'){
            return false;
        }
        boolean res =  helper(grid, 0, 0, 0, 0, row, col);
        return res;
    }

    public boolean helper(char[][] grid, int i, int j, int open, int close, int row, int col){
        if(i<0 || j < 0 || i>row || j > col){

            return false;
        }
        else if((i==row) && (j==col) && (open==close+1)){
            dp[i][j][open - close] = true;
            return true;
        }
        else if(dp[i][j][open - close] != null){
            return dp[i][j][open - close];
        }
        else if(grid[i][j] == '('){
            boolean res =  helper(grid, i+1, j, open+1, close, row, col) || helper(grid, i, j+1, open+1, close, row, col);
            dp[i][j][open - close] = res;
            return res;
        }
        else if( (open > close) && (grid[i][j] == ')')){
            boolean res = helper(grid, i+1, j, open, close+1, row, col) || helper(grid, i, j+1, open, close+1, row, col);
            dp[i][j][open - close] = res;
            return res;
        }
        else{
            dp[i][j][open-close] = false;
            return false;
        }

    }

}
