class Solution {
public:
    int vis[101][101][200], n, m;
    bool possible = 0;
    int fx[2] = {+1, +0};
    int fy[2] = {+0, +1};

    void dfs(int x, int y, int cum, vector<vector<char>>& grid) {  
        if (possible) return;
        vis[x][y][cum] = true;
        int sum = cum + (grid[x][y] == '(' ? 1 : -1);

        if (sum < 0) return ;

        if (x == n - 1 && y == m - 1) {
            possible |= (sum == 0);
            return ;
        }

        for (int i = 0; i < 2; i++) {
            int nx = x + fx[i];
            int ny = y + fy[i];
            if (nx < 0 || ny < 0 || nx >= n || ny >= m) continue;
            if (!vis[nx][ny][sum]) {
                dfs(nx, ny, sum, grid);
            }
        }

    }

    bool hasValidPath(vector<vector<char>>& grid) {
        n = grid.size();
        m = grid[0].size();
        dfs(0, 0, 0, grid);
        return possible;
    }
};
