from functools import cache

class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        m = len(grid)
        n = len(grid[0])
        if grid[m-1][n-1] == "(":
            return False

        @cache
        def dp(x, y, o):
            if x == m - 1 and y == n - 1:
                return o == 1 # assert grid[m-1][n-1] == ")"
            if grid[x][y] == "(":
                o += 1
            else: # ")"
                if o == 0:
                    return False
                o -= 1
            if x + 1 < m and dp(x + 1, y, o):
                return True
            if y + 1 < n and dp(x, y + 1, o):
                return True
            # unreachable branch

        return dp(0, 0, 0)
