            auto& u=dp[i][j];
            if(i==0&&j==0)
            {
                if(grid[i][j]=='(')
                    u=1<<1;
                else
                    return false;
                continue;
            }
            if(i>0)
                v2=dp[i-1][j];
            if(j>0)
                v1=dp[i][j-1];

            if(grid[i][j]==')')
            {
                if(v1&&v1->none())
                    v1=std::nullopt;
                if(v2&&v2->none())
                    v2=std::nullopt;
            }

            if(v1&&v2)
            {
                if(grid[i][j]=='(')
                    u=*v1<<1|*v2<<1;
                else
                    u=*v1>>1|*v2>>1;
            }
            else if(v1)
            {
                if(grid[i][j]=='(')
                    u=*v1<<1;
                else
                    u=*v1>>1;
            }
            else if(v2)
            {
                if(grid[i][j]=='(')
                    u=*v2<<1;
                else
                    u=*v2>>1;
            }
            else
                u=std::nullopt;
        }
        auto& v=dp[m-1][n-1];
        if(v)
            return v->test(0);
        else
            return false;
    }
};
```

