/**
 * Time Complexity: O(rows * cols * (rows + cols))
 * Space Complexity: O(rows * cols * (rows + cols))
 * where `rows` is the number of the rows of the matrix `grid`
 *       `cols` is the number of the columns of the matrix `grid`
 */
class Solution {
 public:
  bool hasValidPath(const vector<vector<char>> &grid) {
    constexpr int range = 2;
    constexpr char open = '(';
    const int rows = static_cast<int>(grid.size());
    const int cols = static_cast<int>(grid.front().size());
    bool dp[range][cols][rows + cols];
    memset(dp, 0, sizeof(dp));
    int previous = 0;
    int current = 1;
    dp[previous][0][0] = true;
    for (const vector<char> &row : grid) {
      for (int c = 0; c < cols; ++c) {
        const int cell = row[c] == open ? 1 : -1;
        for (int previous_cell = max(0, -cell); previous_cell < rows + cols; ++previous_cell) {
          const int current_cell = previous_cell + cell;
          if (dp[previous][c][previous_cell] || (c > 0 && dp[current][c - 1][previous_cell])) {
            dp[current][c][current_cell] = true;
          }
        }
      }

      previous ^= 1;
      current ^= 1;
      memset(dp[current], 0, sizeof(dp[current]));
    }
    return dp[previous][cols - 1][0];
  }
};
```

