class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:

        m, n = len(grid), len(grid[0])
        if grid[0][0] != "(" or grid[-1][-1] != ")":
            return False

        dp = defaultdict(lambda : -1)


        def backtracking(i,j, stack):
            if dp[(i,j, stack)] != -1:
                return dp[(i,j, stack)]

            if i == m-1 and j == n-1:
                if len(stack)==0:
                    dp[(i,j, stack)]= True
                    return True
                dp[(i,j, stack)] = False
                return False


            if i+1 < m:
                if len(stack) and grid[i+1][j] == ")":
                    if backtracking(i+1, j, stack[:-1]):
                        dp[(i,j, stack)]= True
                        return True
                elif (len(stack) == 0 and grid[i+1][j] != ")" ) or grid[i+1][j] == "(":
                    if backtracking(i+1, j, stack+grid[i+1][j]):
                        dp[(i,j, stack)]= True
                        return True

            if j+1 < n:
                if len(stack) and grid[i][j+1] == ")":
                    if backtracking(i, j+1, stack[:-1]):
                        dp[(i,j, stack)]= True
                        return True
                elif (len(stack) == 0 and grid[i][j+1] != ")" )  or grid[i][j+1] == "(":
                    if backtracking(i, j+1, stack+grid[i][j+1]):
                        dp[(i,j, stack)]= True
                        return True
            dp[(i,j, stack)]= False
            return False

        return backtracking(0,0, grid[0][0])






