class Solution {
    int M,N, MID;
    Boolean[][][] dp;
    public boolean hasValidPath(char[][] grid) {
        M = grid.length;
        N = grid[0].length;
        MID = (M+N)/2;
        dp = new Boolean[M][N][MID+1];
        return rec(0, 0, grid, 0);
    }

    boolean rec(int i, int j, char[][] grid, int sum) {
        if(i==M-1 && j==N-1) {
            if(sum==1 && grid[i][j]==')') {
                return true;
            }
            return false;
        }

        if(dp[i][j][sum]!=null)
        return dp[i][j][sum];

        boolean ans = false;
        if(grid[i][j]=='(' && sum<MID) {
            if(i<M-1)
            ans = ans || rec(i+1,j, grid, sum+1);
            if(j<N-1)
            ans = ans || rec(i, j+1, grid, sum+1);
        } else if(grid[i][j]==')' && sum>0){
            if(i<M-1)
            ans = ans || rec(i+1,j, grid, sum-1);
            if(j<N-1)
            ans = ans || rec(i, j+1, grid, sum-1);
        }
        dp[i][j][sum]=ans;
        return ans;
    }
}
