class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:

        row, col = len(grid), len(grid[0])
        if grid[0][0] == ")" or grid[-1][-1] == "(" or (row + col - 1) % 2:
            return False

        dp = defaultdict(set)
        dp[0, 0] = {1}

        for r in range(row):
            for c in range(col):
                sign = 1 if grid[r][c] == "(" else -1
                dp[r, c] |= set([val + sign  for val in dp[(r -1, c)] | dp[(r, c- 1)] if val + sign >= 0])

        return 0 in dp[(row-1, col -1)]

