class Solution {
public:
    bool hasValidPath(vector<vector<char>>& grid) {

        int n = grid.size();
        int m = grid[0].size();

        if(grid[0][0] == ')' || grid[n-1][m-1] == '('){
            return false;
        }

        // '(' -> + 1
        // ')' -> -1
        vector<vector<vector<bool>>> dp(n,vector<vector<bool>> (m,vector<bool> (203,false)));

        dp[0][0][1] = true;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(i==0 && j==0){
                    continue;
                }

                for(int k=0;k<202;k++){
                    if(i>0){
                        if(grid[i][j] == '('){
                            if(k>0){
                               dp[i][j][k] = dp[i][j][k] | dp[i-1][j][k-1]; 
                            }
                        }else{
                            dp[i][j][k] = dp[i][j][k] | dp[i-1][j][k+1];
                        }

                    }
                    if(j>0){
                        if(grid[i][j] == '('){
                            if(k>0){
                               dp[i][j][k] = dp[i][j][k] | dp[i][j-1][k-1]; 
                            }
                        }else{
                            dp[i][j][k] = dp[i][j][k] | dp[i][j-1][k+1];
                        }
                    }

                }

            }
        }
        return dp[n-1][m-1][0];
    }
};

