class Solution {
public:
    int solve(vector<vector<vector<int>>> &dp, vector<vector<char>> &grid, int i, int j, int open)
    {
        int n = grid.size(), m = grid[0].size();
        if(open < 0)return false;
        if(dp[i][j][open]!=-1)return dp[i][j][open];
        if(i == n-1 && j == m-1)
        {
            if(grid[i][j] == ')' && open == 1)return dp[i][j][open] = true;
            return dp[i][j][open] = false;
        }
        bool ans = false;
        if(grid[i][j] == '(')
        {
            if(i+1 < n)ans = ans | solve(dp, grid, i+1, j, open+1);
            if(j+1 < m)ans = ans | solve(dp, grid, i, j+1, open+1);
        }
        else
        {
            if(i+1 < n)ans = ans | solve(dp, grid, i+1, j, open-1);
            if(j+1 < m)ans = ans | solve(dp, grid, i, j+1, open-1);
        }
        return dp[i][j][open] = ans;
    }
    bool hasValidPath(vector<vector<char>>& grid){
        int n = grid.size(), m = grid[0].size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>> (m, vector<int> (m+n, -1)));
        return solve(dp, grid, 0,0,0);
    }
};
