/**
 * @param {character[][]} grid
 * @return {boolean}
 */
var hasValidPath = function(grid) {
  let visiting = new Set();
  let cache = new Set();
  return dfs([0, 0], grid, visiting, 0, cache);
};

function dfs(cell, grid, visiting, currentSum, cache) {
  let [row, col] = cell;
  let val = grid[row][col] === "(" ? 1 : -1;
  let remainingSum = 0 - (currentSum + val);
  if (cache.has(`${row}_${col}_${remainingSum}`)) {
    return false;
  }
  if (currentSum + val < 0) {
    return false;
  }

  if (row === grid.length - 1 && col === grid[0].length - 1) {
    // reached the end
    return (currentSum + val === 0);
  }

  // add it to visiting
  visiting.add(`${row}_${col}`);

  // visit all neighbors
  for (let [i, j] of [[1, 0], [0, 1]]) {
    if (isValid(grid, row + i, col + j) &&
      !visiting.has(`${row + i}_${col + j}`)) {

      // add neighbor to search
      if (dfs([row + i, col + j], grid, visiting, currentSum + val, cache)) {
        return true;
      }
    }
  }

  visiting.delete(`${row}_${col}`);
  cache.add(`${row}_${col}_${remainingSum}`);

  return false;
}

function isValid(grid, row, col) {
  if (row >= 0 && col >= 0 && row < grid.length && col < grid[0].length) {
    return true;
  }
  return false;
}
