class Solution {
public:

    bool solve(int x, int y, int x_bound, int y_bound, int score,vector<vector<char>>&table, vector<vector<vector<int>>>&dp){
           // at each point score must be greater than or equal to 0
         bool move_down=false, move_right= false;

          if(x < x_bound && y < y_bound){


                  if(table[x][y]=='(')++score;
                  else score --;

                  if(score < 0)return false;

                    if(dp[x][y][score]!=-1)return dp[x][y][score];

                   move_down = solve(x+1,y,x_bound,y_bound, score,table,dp);
                   move_right = solve(x, y+1, x_bound, y_bound, score, table,dp);
          }

          else if( (x==x_bound-1 && y==y_bound ) || (x==x_bound && y==y_bound-1)){

                 return score==0;
          }


          return dp[x][y][score]=move_down|| move_right;


    }
    bool hasValidPath(vector<vector<char>>& grid) {

        vector<vector<vector<int>>>dp(grid.size()+1, vector<vector<int>>(grid[0].size()+1,vector<int>(grid.size()+grid[0].size(),-1)));

        return solve(0,0,grid.size(), grid[0].size(),0,grid,dp);

    }
};
