class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        numRows, numCols = len(grid), len(grid[0])
        dp = [[[-1] * (numRows + numCols + 1) for _ in range(numCols)] for _ in range(numRows)]

        def dfs(row: int, col: int, balance: int) -> bool:
            nonlocal grid, dp
            if row >= numRows or col >= numCols:
                return 0
            if grid[row][col] == '(':
                balance += 1
            else:
                balance -= 1
            if balance < 0:
                return 0
            if row == numRows - 1 and col == numCols - 1:
                return balance == 0
            if dp[row][col][balance] != -1:
                return dp[row][col][balance]
            dp[row][col][balance] = dfs(row + 1, col, balance) or dfs(row, col + 1, balance)
            return dp[row][col][balance]

        return dfs(0, 0, 0)
