class Solution {
public:
    bool ans = false;
    int dp[100][100][200];
    bool solve(int i, int j, int balance, vector<vector<char>>& g) {

        if(balance < 0) return false;

        if(i == g.size() - 1 && j == g[0].size() - 1)  return balance == 0;
        if(dp[i][j][balance] != -1) return dp[i][j][balance];



        bool ans = false;
        if(i + 1 < g.size()) {
            int newBalance = (g[i + 1][j] == '(' ? balance + 1 : balance - 1);
            if(newBalance >= 0) ans = ans or solve(i + 1, j, newBalance, g);
            if(ans) return dp[i][j][balance] = ans;
        } 

        if(j + 1 < g[0].size()) {
            int newBalance = (g[i][j + 1] == '(' ? balance + 1 : balance - 1);
            if(newBalance >= 0) ans = ans or solve(i, j + 1, newBalance, g);
            if(ans) return dp[i][j][balance] = ans;
        }

        return dp[i][j][balance] = ans;
    }
    bool hasValidPath(vector<vector<char>>& g) {
        memset(dp, -1, sizeof(dp));
