class Solution {
public:
    int m,n;
    int dx[2]={1,0};
    int dy[2]={0,1};
    vector<vector<int>>vis;
    bool hasValidPath(vector<vector<char>>& grid) 
    {
        m=grid.size();
        n=grid[0].size();
        vis.resize(m,vector<int>(n,false));
        if(grid[0][0]==')')
        {
            return false;
        }
        queue<pair<int,pair<int,int>>>q;
        q.push({0,{0,1}});
        vis[0][0]=true;
        while(!q.empty())
        {   
            auto it=q.front();
            q.pop();
            int i=it.first;
            int j=it.second.first;
            int cnt=it.second.second;
            if(i==m-1 && j==n-1 && cnt==0)
            {
                return true;
            }
            for(int k=0;k<2;k++)
            {
                int newi=i+dx[k];
                int newj=j+dy[k];
                if(newi>=0 && newi<m && newj>=0 && newj<n && grid[newi][newj]==')' && !vis[newi][newj])
                {
                    vis[newi][newj]=true;
                    q.push({newi,{newj,cnt-1}});
                    vis[newi][newj]=false;
                }
                else if(newi>=0 && newi<m && newj>=0 && newj<n && grid[newi][newj]=='(' && !vis[newi][newj])
                {
                    vis[newi][newj]=true;
                    q.push({newi,{newj,cnt+1}});
                    vis[newi][newj]=false;
                }
            }
        }
        return false;
    }
};

