func hasValidPath(grid [][]byte) bool {
	var (
		m = len(grid)
		n = len(grid[0])
		l = m + n
	)
	dp := make([][][]bool, m+1)
	for i := 0; i <= m; i++ {
		dp[i] = make([][]bool, n+1)
		for j := 0; j <= n; j++ {
			dp[i][j] = make([]bool, l+1)
		}
	}
	dp[0][1][0] = true
	for i, x := 0, 1; i < m; i, x = i+1, x+1 {
		for j, y := 0, 1; j < n; j, y = j+1, y+1 {
			for k := 0; k <= l; k++ {
				dp[x][y][k] = dp[i][y][k] || dp[x][j][k]
			}
			if grid[i][j] == '(' {
				for k := l; k >= 1; k-- {
					dp[x][y][k] = dp[x][y][k-1]
				}
				dp[x][y][0] = false
			} else {
				for k := 0; k < l; k++ {
					dp[x][y][k] = dp[x][y][k+1]
				}
			}
		}
	}
	return dp[m][n][0]
}
