class Solution:
    def hasValidPath(self, x: List[List[str]]) -> bool:
        m,n=len(x),len(x[0])    
        @lru_cache(None)
        def f(i,j,c):
            if i==m-1 and j==n-1:return not c
            ans=False
            for d,e in ((i+1,j),(i,j+1)):
                if d<m and e<n:
                    if x[d][e]=='(':
                        ans=ans or f(d,e,c+1)
                    else:
                        if c:
                            ans=ans or f(d,e,c-1)
            return ans
        return False if x[0][0]==')' else f(0,0,1) 
