    public boolean hasValidPath(char[][] grid) {
        if (null==grid) {
            return false;
        }
        int rc=grid.length;
        int cc=grid[0].length;
        if (grid[0][0]!='('||grid[rc-1][cc-1]!=')') {
            return false;
        }
        int[][][] dp=new int[101][101][202];
        for (int i=0;i<101;i++) {
            for (int j=0;j<101;j++) {
                Arrays.fill(dp[i][j],-1);
            }
        }
        int b=helper(1,0,0,rc,cc,grid,dp);
        return b==1;
    }

    private int helper(int count, int ri, int ci, int rc, int cc, char[][] grid, int[][][] dp) {
        if (count<0) {
            return 0;
        }
        if (-1!=dp[ri][ci][count]) {
            return dp[ri][ci][count];
        }
        if (count==0&&ri==rc-1&&ci==cc-1) {
            return 1;
        }
        int ret=-1;
        if (ri<rc-1) {
            if (grid[ri+1][ci]=='(') {
                ret=helper(count+1,ri+1,ci,rc,cc,grid,dp);
            }
            if (ret!=1&&grid[ri+1][ci]==')') {
                ret=helper(count-1,ri+1,ci,rc,cc,grid,dp);
            }
        }
        if (ret!=1&&ci<cc-1) {
            if (grid[ri][ci+1]=='(') {
                ret=helper(count+1,ri,ci+1,rc,cc,grid,dp);
            }
            if (ret!=1&&grid[ri][ci+1]==')') {
                ret=helper(count-1,ri,ci+1,rc,cc,grid,dp);
            }
        }
        if (ret!=1) {
            dp[ri][ci][count]=0;
        }
        return ret;
