class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        m, n = len(grid), len(grid[0])

        @cache
        def dfs(i, j, trace):
            nonlocal m, n
            trace += (1 if grid[i][j] == '(' else -1)
            if trace < 0:
                return False
            if i == m-1 and j == n-1 and trace == 0:
                return True
            right = down = False
            if i + 1 < m:
                right = dfs(i+1, j, trace)
            if j + 1 < n:
                down = dfs(i, j+1, trace)
            return right or down

        return dfs(0,0,0)
