class Solution {
    public boolean hasValidPath(char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        Boolean[][][] memory = new Boolean[m][n][(m+n)/2 + 1];
        return dfs(0, 0, 0, grid, memory);
    }

    private boolean dfs(int leftCnt, int x, int y, char[][] grid, Boolean[][][] memory) {
        int m = grid.length;
        int n = grid[0].length;
        // boundary check
        if(x < 0 || y < 0 || x >= m || y >= n) {
            return false;
        }

        if(grid[x][y] == '(') {
            leftCnt++;
        } else if (grid[x][y] == ')') {
            leftCnt--;
        }


        if(leftCnt < 0 || leftCnt > (m+n)/2) {
            // memory[x][y][leftCnt] = false;
            return false; 
        }

       if(memory[x][y][leftCnt] != null) return memory[x][y][leftCnt]; 

        if(x == m -1 && y == n -1 && leftCnt == 0) {
            memory[x][y][leftCnt]  = true;
            return true;
        }

        boolean valid =  dfs(leftCnt, x + 1, y, grid, memory) || dfs(leftCnt, x, y + 1, grid, memory);
        memory[x][y][leftCnt]  = valid;
        return valid;
    }
}
