class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        if grid[-1][-1] == "(": return False
        if grid[0][0] == ")": return False
        m, n = len(grid), len(grid[0])
        if (m+n) % 2 == 0: return False
        @cache
        def pos(i, j, cur):
            if cur == ")": return False
            if i == m-1 and j == n-1:
                if cur == "(": return True
                return False
            if i >= m: return False
            if j >= n: return False
            if cur and grid[i][j] == ")" and cur[-1] == "(":
                cur = cur[:-1]
            else:
                cur += grid[i][j]
            return pos(i+1, j, cur) or pos(i, j+1, cur)
        return pos(0,0,"")
