var hasValidPath = function(a) {
    const [m, n] = [a.length-1, a[a.length-1].length - 1]
    const me = {}
    const getNeighbours = ([i, j]) => [[i+1, j], [i, j+1]].filter(([i, j]) => a[i]?.[j])
    const hasValidPathUtil = ([i, j], s) => {
        if(me[[i, j, s]] !== undefined) {
            return me[[i, j, s]]
        }
        let result = false;
        const val = a[i][j]
        if (val === '\)') {
            if(s.length) s.pop()
            else {
                me[[i, j, s]] = result;
                return result;
            }
        } else {
            s.push('\(')
        }
        if(i === m && j === n && s.length === 0) {

            result = true
        } else {
            const neighbours = getNeighbours([i, j])
            for(let x = 0; x< neighbours.length; x++) {
                const [i, j] = neighbours[x]
                if(hasValidPathUtil([i, j], [...s])) {
                    result = true
                    break
                }
            }
        }
        me[[i, j, s]] = result;
        return result;
    }

    let res = hasValidPathUtil([0, 0], [])
    return res
};
