public class Solution
{
    private int[][][] dp = new int[100][][];

    public bool DFS(char[][] grid, int i, int j, int k)
    {
        if (i >= grid.Length || j >= grid[0].Length)
        {
            return false;
        }

        if (grid[i][j] == '(')
        {
            k++;
        }
        else
        {
            k--;
        }

        if (k < 0)
        {
            return false;
        }

        if (i == grid.Length - 1 && j == grid[0].Length - 1)
        {
            return k == 0;
        }

        if (dp[i][j][k] != -1)
        {
            return Convert.ToBoolean(dp[i][j][k]);
        }

        dp[i][j][k] = Convert.ToInt32(DFS(grid, i + 1, j, k) | DFS(grid, i, j + 1, k));
        return Convert.ToBoolean(dp[i][j][k]);
    }

    public bool HasValidPath(char[][] grid)
    {
        for (int i = 0; i < dp.Length; i++)
        {
            dp[i] = new int[100][];
            for (int j = 0; j < dp[i].Length; j++)
            {
                dp[i][j] = new int[205];
                Array.Fill(dp[i][j], -1);
            }
        }
        return DFS(grid, 0, 0, 0);
