class Solution {
    public boolean hasValidPath(char[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;

        boolean dp[][][] = new boolean[2][cols][rows+cols];

        for(int row=rows-1;row>=0;row--){

            for(int col=cols-1;col>=0;col--){
                char bracket = grid[row][col];

                for(int count = 0;count<rows+cols;count++){
                    int nextCount = count;
                    if(bracket=='(') nextCount++;
                    else nextCount--;

                    if(nextCount<0) continue;

                    if(row==rows-1&&col==cols-1) dp[0][col][count] = (nextCount==0);
                    else if(nextCount<rows+cols){
                        if(col+1<cols) dp[0][col][count]|=dp[0][col+1][nextCount];
                        if(row+1<rows) dp[0][col][count]|=dp[1][col][nextCount];
                    }
                }
            }

            if(row>0){
                for(int col=cols-1;col>=0;col--){
                    for(int count = 0;count<rows+cols;count++){
                        dp[1][col][count] = dp[0][col][count];
                        dp[0][col][count] = false;
                    }
                }
            }
        }

        return dp[0][0][0];
    }
}
