        boolean dp[][][] = new boolean[rows][cols][rows+cols];

        for(int row=rows-1;row>=0;row--){

            for(int col=cols-1;col>=0;col--){
                char bracket = grid[row][col];

                for(int count = 0;count<rows+cols;count++){
                    int nextCount = count;
                    if(bracket=='(') nextCount++;
                    else nextCount--;

                    if(nextCount<0) continue;

                    if(row==rows-1&&col==cols-1) dp[row][col][count] = (nextCount==0);
                    else if(nextCount<rows+cols){
                        if(col+1<cols) dp[row][col][count]|=dp[row][col+1][nextCount];
                        if(row+1<rows) dp[row][col][count]|=dp[row+1][col][nextCount];
                    }
                }
            }
        }

        return dp[0][0][0];
    }
}
