```import java.util.*;

class Solution {
    public boolean hasValidPath(char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        Set<Integer>[][] sets = new Set[m][n];

        if (grid[0][0] != '(' || grid[m-1][n-1] != ')') {
            return false;
        }

        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                sets[i][j] = new HashSet<>();
                if(i == 0 && j == 0) {
                    sets[0][0].add(1);
                    continue;
                }
                int toAdd = grid[i][j] == ')' ? -1 : 1;

                if (i-1 >= 0) {
                    Iterator<Integer> it = sets[i-1][j].iterator();
                    while(it.hasNext()) {
                        int val = it.next() + toAdd;
                        if (val >= 0) {
                            sets[i][j].add(val);
                        }

                    }
                }
                if (j-1 >= 0) {
                    Iterator<Integer> it = sets[i][j-1].iterator();
                    while (it.hasNext()) {
                        int val = it.next() + toAdd;
                        if (val >= 0) {
                            sets[i][j].add(val);
                        }
                    }

                }
            }
        }

        return sets[m-1][n-1].contains(0);
    }
}
