        if (col > grid.length - 1 || row > grid[0].length - 1) return false;
        parenthesesCounter += grid[col][row] == '(' ? 1 : - 1;
        if (parenthesesCounter < 0 || parenthesesCounter > (grid.length + grid[0].length) / 2 || visited[col][row][parenthesesCounter]) return false;
        visited[col][row][parenthesesCounter] = true;
        if (col == grid.length - 1 && row == grid[0].length - 1 && parenthesesCounter == 0) return true;
        return dfs(col + 1, row, grid, visited, parenthesesCounter) || dfs(col, row + 1, grid, visited, parenthesesCounter);
