class Solution {
    public boolean hasValidPath(char[][] grid) {
        int n = grid.length;
        int m = grid[0].length;
        char L = '(';
        char R = ')';

        if(!(grid[0][0] == L && grid[n - 1][m - 1] == R)) return false;

        Set<Integer>[][] dp = new HashSet[n][m];
        Set<Integer> init = new HashSet<>();
        init.add(1);
        dp[0][0] = init;

        // top
        for(int j = 1; j < m; j++){
            Set<Integer> curr = new HashSet<>();

            if(grid[0][j] == R){
                for(int val : dp[0][j - 1]){
                    if(val >= 0){
                        curr.add(val - 1);
                    }
                }
            }else{
                for(int val : dp[0][j - 1]){
                    if(val >= 0){
                        curr.add(val + 1);
                    }
                }
            }
            dp[0][j] = curr;
        }

        // left
        for(int i = 1; i < n; i++){
            Set<Integer> curr = new HashSet<>();
            if(dp[i - 1][0].contains(-1)){
                curr.add(-1);
                dp[i][0] = curr;
                continue;
            }


            if(grid[i][0] == R){
                for(int val : dp[i - 1][0]){
                    if(val >= 0){
                        curr.add(val - 1);
                    }
                }
            }else{
                for(int val : dp[i - 1][0]){
                    if(val >= 0){
                        curr.add(val + 1);
                    }
                }
            }
            dp[i][0] = curr;
        }

        // regular
        for(int i = 1; i < n; i++){
            for(int j = 1; j < m; j++){
                Set<Integer> curr = new HashSet<>();

                if(dp[i - 1][j].contains(-1) && dp[i][j - 1].contains(-1)){
                    curr.add(-1);
                    dp[i][j] = curr;
                    continue;
                }

                if(grid[i][j] == R){
                    for(int val : dp[i][j - 1]){
                        if(val - 1 >= 0){
                            curr.add(val - 1);
                        }
                    }

                    for(int val : dp[i - 1][j]){
                        if(val - 1 >= 0){
                            curr.add(val - 1);
                        }
                    }

                }else{
                    for(int val : dp[i][j - 1]){
                        if(val >= 0){
                            curr.add(val + 1);
                        }
                    }

                    for(int val : dp[i - 1][j]){
                        if(val >= 0){
                            curr.add(val + 1);
                        }
                    }
                }

                dp[i][j] = curr;
            }
        }

        return dp[n - 1][m - 1].contains(0);
    }
}
