```java
class Solution {
    public boolean hasValidPath(char[][] grid) {
        int m = grid.length;
        int n = grid[0].length;

        Set<Integer>[][] dp = new Set[m][n];
        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                dp[i][j] = new HashSet<Integer>();
            }   
        }
        dp[0][0].add(grid[0][0] == '(' ? 1 : -1);

        for(int i = 0; i < m; i++) {
            for(int j = 0; j < n; j++) {
                int inc = grid[i][j] == '(' ? 1 : -1;
                if (i-1 >= 0) addValue(dp[i-1][j], dp[i][j], inc);
                if (j-1 >= 0) addValue(dp[i][j-1], dp[i][j], inc);
            }
        }

        return dp[m-1][n-1].contains(0);
    }

    private void addValue(Set<Integer> src, Set<Integer> dst, int inc) {
        for(Integer value : src) {
            if (value.intValue() < 0) continue;
            int val = value.intValue() + inc;
            if (val >= 0) dst.add(val);
        } 
    }
}
