class Solution {
    int m, n;
public:
    bool hasValidPath(vector<vector<char>>& grid) {

        m = grid.size(), n = grid[0].size();
        vector<vector<vector<int>>> dp(100, vector<vector<int>> (100, vector<int> (205, -1)));
        return helper(grid, 0, 0, 0, dp);
    }

    bool helper(vector<vector<char>> &grid, int i, int j, int a, vector<vector<vector<int>>> &dp)
    {
        if(i >= m || j >= n)
            return !a && (i == m - 1 || j == n - 1);

        if(grid[i][j] == '(')
            a++;
        else
            a--;

        if(a < 0)
            return false;

        if(dp[i][j][a] != -1)
            return dp[i][j][a];

        return dp[i][j][a] = helper(grid, i + 1, j, a, dp) || helper(grid, i, j + 1, a, dp);
    }
};

