def hasValidPath(self, grid: List[List[str]]) -> bool:
        # in this function we check how many "(" are needed at location [i][j]
        # starting from grid[m-1][n-1], and move up and left
        # if grid[m-1][n-1]=="(", there is no way to form a valid parentheses string path
        # else, the "(" needed is 1. so we start with 1
        # if grid[i][j]==")", add 1 to the value
        # if grid[i][j]=="(", minus 1 to the value
        # only keep records of values greater or equal to 0
        m = len(grid)
        n = len(grid[0])
        # use a matrix brackets to keep records of how many "(" are needed at each location
        brackets = [[0]*n for i in range(m)]
        for i in range(m):
            for j in range(n):
                brackets[i][j] = set()


        j = n-1
        i = m-1
        if grid[i][j]==")":
            # 1. initialize brackets[m-1][n-1]
            brackets[i][j].add(1)
            # 2. initialize the last column of values based on brackets[i+1][j]. note that we only need to keep values greater or equal to 0.
            i -= 1
            while i>=0:
                if grid[i][j]==")":
                    if len(brackets[i+1][j])>0:
                        brackets[i][j].add(list(brackets[i+1][j])[0] + 1)
                else:
                    if len(brackets[i+1][j])>0:
                        if list(brackets[i+1][j])[0] - 1>=0:
                            brackets[i][j].add(list(brackets[i+1][j])[0] - 1)
                i -= 1
            # 3. initialize the last row of values based on brackets[i][j+1]
            j = n-2
            i = m-1
            while j>=0:
                if grid[i][j]==")":
                    if len(brackets[i][j+1])>0:
                        brackets[i][j].add(list(brackets[i][j+1])[0] + 1)
                else:
                    if len(brackets[i][j+1])>0:
                        if list(brackets[i][j+1])[0] - 1>=0:
                            brackets[i][j].add(list(brackets[i][j+1])[0] - 1)
                j -= 1

            # 4. from brackets[m-2][n-2], for brackets[i][j]: obtain how many "(" are needed based on numbers from brackets[i][j+1] and brackets[i+1][j]
            i = m-2
            while i>=0:
                j = n-2
                while j>=0:
                    if grid[i][j]==")":
                        for x in brackets[i][j+1]|brackets[i+1][j]:
                            brackets[i][j].add(x + 1)

                    else:
                        for x in brackets[i][j+1]|brackets[i+1][j]:
                            if x-1>=0:
                                brackets[i][j].add(x - 1)

                    j -= 1
                i -= 1    

            # 5. if 0 is needed, then return True; otherwise, return False
            return (0 in brackets[0][0])
        else:
            # if the last element is "(", then there's no way to form a valid parentheses string path
            return False
