    int sol(int x,int y,int ob,int cb,int &p,vector<vector<char>>& grid)
    {
        if(cb>ob || (ob>p || cb>p))
        {
            return 0;
        }

        if(x==grid.size()-1 && y==grid[0].size()-1)
        {
            if(ob==cb)
            {
                return 1;
            }

            return 0;
        }

        if(dp[x][y][ob]!=-1)
        {
            return dp[x][y][ob];
        }

        int ans=0;

        if(x+1<grid.size())
        {
            if(grid[x+1][y]=='(')
            ans=ans|sol(x+1,y,ob+1,cb,p,grid);
            else
            ans=ans|sol(x+1,y,ob,cb+1,p,grid);
        }

        if(y+1<grid[0].size())
        {
            if(grid[x][y+1]=='(')
            ans=ans|sol(x,y+1,ob+1,cb,p,grid);
            else
            ans=ans|sol(x,y+1,ob,cb+1,p,grid);
        }

        return dp[x][y][ob]=ans;
    }

    bool hasValidPath(vector<vector<char>>& grid) 
    {
        memset(dp,-1,sizeof(dp));
        int n=grid.size(),m=grid[0].size();
        if((n+m-1)%2 || grid[0][0]==')' || grid[n-1][m-1]=='(')
        {
            return false;
        }

        int p=(n+m-1)/2; // this is the size of final valid string and it must have (n+m-1)/2 opening bracket and closing bracket and hence opening and closing bracket should not cross (n+m-1)/2.

        return sol(0,0,1,0,p,grid);
    }

