class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        m, n = len(grid), len(grid[0])

        size = m+n-1
        if size % 2 == 1 or grid[0][0] == ')' or grid[m-1][n-1] == '(':
            return False

        @cache
        def trav(r, c, cnt, step):
            if grid[r][c] == '(':
                newCnt = cnt+1
            else:
                newCnt = cnt-1

            if step == size:
                if newCnt == 0:
                    return True
                return False

            diffs = [(0,1), (1,0)]
            for dr, dc in diffs:
                newR, newC = r+dr, c+dc

                if newR < m and newC < n:
                    if newCnt > 0:
                        if trav(newR, newC, newCnt, step+1):
                            return True
                    elif grid[newR][newC] == '(':
                        if trav(newR, newC, newCnt, step+1):
                            return True

            return False

        return trav(0, 0, 0, 1)

