class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        def check(i, j, x, memo=set()):
            if i >= n or j >= m:
                return False

			# x is the parenthesis score till now; add by 1 if the cell is ( else subtract by 1
            x += 1 if grid[i][j] == '(' else -1

			# If the score is negative then the sequence is already invalid
			# or if the score is greater than half of the path length then the sequence is invalid
			# or if it is already visited then return False
            if x < 0 or x > (m+n)//2 or (i,j,x) in memo:
                return False

            memo.add((i,j,x))


			# if it's the last cell and score is 0 then it is a valid sequence
            if i == n-1 and j == m-1 and x == 0:
                return True

            return check(i+1, j, x, memo) or check(i, j+1, x, memo)

        n, m = len(grid), len(grid[0])

		# if the path length is odd: return False
		# if the first cell is ): return False
		# if the last cell is (: return False
        if (n+m-1)%2 or grid[0][0] == ')' or grid[-1][-1] == '(':
            return False

        return check(0, 0, 0)
