class Solution:
    def hasValidPath(self, grid: List[List[str]]) -> bool:
        n=len(grid)-1
        m=len(grid[0])-1
        @cache
        def dp(i,j,o):
            if i==n and j==m:return o==0
            ni=i+1
            if ni<=n:
                if grid[ni][j]=="(":
                    if dp(ni,j,o+1):return True
                elif (o-1)>=0:
                    if dp(ni,j,o-1):return True
            nj=j+1
            if nj<=m:
                if grid[i][nj]=="(":
                    if dp(i,nj,o+1):return True
                elif (o-1)>=0:
                    if dp(i,nj,o-1):return True
            return False
        if grid[0][0]!="(":return False
        return dp(0,0,1)
