class Solution {
    public boolean hasValidPath(char[][] grid) {
        int row = grid.length;
        int col = grid[0].length;
        int MID = (row + col + 1) / 2;
        if(grid[0][0] != '(' || grid[row - 1][col - 1] != ')') return false;
        boolean[][][] dp = new boolean[row][col][MID + 1];
        dp[0][0][1] = true;
        for(int i = 1; i < row; i++) {
            for(int j = 0; j <= MID; j++) {
                if(grid[i][0] == '(' && j - 1 >= 0) {
                    dp[i][0][j] = dp[i - 1][0][j - 1];
                }
                else if(grid[i][0] == ')' && j + 1 <= MID) {
                    dp[i][0][j] = dp[i - 1][0][j + 1];
                }
            }
        }

        for(int i = 1; i < col; i++) {
            for(int j = 0; j <= MID; j++) {
                if(grid[0][i] == '(' && j - 1 >= 0) {
                    dp[0][i][j] = dp[0][i - 1][j - 1];
                }
                else if(grid[0][i] == ')' && j + 1 <= MID) {
                    dp[0][i][j] = dp[0][i - 1][j + 1];
                }
            }
        }

        for(int i = 1; i < row; i++) {
            for(int j = 1; j < col; j++) {
                for(int k = 0; k <= MID; k++) {
                    if(grid[i][j] == '(' && k - 1 >= 0) {
                        dp[i][j][k] = dp[i - 1][j][k - 1] || dp[i][j - 1][k - 1];
