A '(' will add the count by 1, and a ')' will decrease the count by 1.
Track whether a certain position in the matrix with a certain count has been visited or not to prune the dfs.

```
class Solution {
private:
    bool v[100][100][100];

public:
    bool hasValidPath(vector<vector<char>>& grid) {
        return dfs(grid, 0, 0, 0);
    }

    bool dfs(vector<vector<char>>& grid, int i, int j, int cnt) {
        if (i >= grid.size() || j >= grid[0].size()) {
            return false;
        }

        cnt += (grid[i][j] == '(' ? 1 : -1);

        if (cnt < 0 || cnt >= 100 || v[i][j][cnt]) {
            return false;
        }

        if (i == grid.size()-1 && j == grid[0].size()-1 && cnt == 0) {
            return true;
        }

        if (dfs(grid, i, j+1, cnt) || dfs(grid, i+1, j, cnt)) {
            return true;
        }
        else {
            v[i][j][cnt] = true;
            return false;
        }
    }
};
