class Solution {

    public boolean hasValidPath(char[][] grid) {
        if (grid[0][0] == ')' || grid[grid.length - 1][grid[0].length - 1] == '(') return false;
        int n = grid.length;
        int m = grid[0].length;
        Boolean[][][] dp = new Boolean[n][m][n + m];
        return solve(0, 0, grid, 0, dp);
    }

    public boolean solve(int i, int j, char[][] grid, int open, Boolean[][][] dp) {
        if (i >= grid.length || j >= grid[0].length) return false;

        open += grid[i][j] == '(' ? 1 : -1;
        if (open < 0) return false;

        if (i == grid.length - 1 && j == grid[0].length - 1) {
            if (open == 0) return true;
        }
        if (dp[i][j][open] != null) return dp[i][j][open];

        if (solve(i + 1, j, grid, open, dp)) {
            return dp[i][j][open] = true;
        }
        if (solve(i, j + 1, grid, open, dp)) {
            return dp[i][j][open] = true;
        }

        return dp[i][j][open] = false;
    }
}

