        for(i = 1; i < rows; i++){
            if(grid[i][0] == ')'){
                for(j = 0; j < rows + cols - 1; j++){
                    dp[i][0][j] = dp[i - 1][0][j + 1];
                }
            }
            else{
                for(j = 1; j < rows + cols; j++){
                    dp[i][0][j] = dp[i - 1][0][j - 1];
                }
            }
        }

        for(i = 1; i < rows; i++){
            for(j = 1; j < cols; j++){
