        if(dp[i][j][lefts]!=-1) return dp[i][j][lefts];

        if (grid[i][j] == '(')
            lefts++;
        else if(grid[i][j]==')' && lefts>0)
            lefts--;
        else 
            return dp[i][j][lefts]=false;

        bool found = false;
        if(i+1<grid.size())
            found |= recurse( grid, i+1, j, lefts);
        if(j+1<grid[0].size())
            found |= recurse( grid, i, j+1, lefts);

        return dp[i][j][lefts]=found;

    }
