class Solution {
public:
    int minimumObstacles(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<long long>> dist(n+1,vector<long long>(m+1,1e8));
        queue<pair<int,int>> q;
        int cnt = 0;
        if(grid[0][0]==1){
            cnt++;
        }
        q.push({0,0});
        dist[0][0]=cnt;
        vector<vector<int>> dir = {{1,0},{0,1},{-1,0},{0,-1}};
        while(q.empty()==false){
            long long x = q.front().first;
            long long y = q.front().second;
            q.pop();
            for(auto &mov : dir){
                long long newx = x + mov[0];
                long long newy = y + mov[1];
                if(newx>=0 && newx<n && newy>=0 && newy<m && dist[x][y]+grid[newx][newy]<dist[newx][newy]){
                    dist[newx][newy] = dist[x][y]+grid[newx][newy];
                    q.push({newx,newy});
                }
            }
        }
        return dist[n-1][m-1];
    }
};
