```
class Solution {
    public int minimumObstacles(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;

        int[][] dist = new int[n][m];

        for(int[] d: dist){
            Arrays.fill(d,Integer.MAX_VALUE);
        }

        int dx[]={-1,1,0,0};
        int dy[]={0,0,-1,1};

        LinkedList<int[]> q = new LinkedList<>();

        dist[0][0] = grid[0][0];

        q.addFirst(new int[]{dist[0][0],0,0});

        while(!q.isEmpty()){
            int sz=q.size();
            while(sz-->0){
                int[] cur = q.poll();
                int cost = cur[0], x = cur[1], y = cur[2];

                for(int k=0;k<4;k++){
                    int nx=x+dx[k];
                    int ny=y+dy[k];
                    if(nx>=0 && nx<n && ny>=0 && ny<m && dist[nx][ny]>grid[nx][ny]+cost) {
                        dist[nx][ny]=grid[nx][ny]+cost;

                        if(grid[nx][ny]==0){
                            q.addFirst(new int[]{dist[nx][ny],nx,ny});
                        }else{
                            q.addLast(new int[]{dist[nx][ny],nx,ny});
                        }
                    }
                }
            }
        }

        return dist[n-1][m-1];
    }
}
