class Solution {
    public boolean valid(int i,int j,int m,int n,boolean[][] visited){
        return i>=0&&j>=0&&i<m&&j<n&&!visited[i][j];
    }
    public int minimumObstacles(int[][] grid) {
        int m=grid.length;
        int n=grid[0].length;
        int[][] dp=new int[m][n];
        boolean[][] visited=new boolean[m][n];
        PriorityQueue<pair> q=new PriorityQueue<pair>((a,b)->(a.val-b.val));
        q.add(new pair(0,0,0));
        visited[0][0]=true;
        while(q.size()!=0){
            pair p=q.poll();
            if(valid(p.ii-1,p.jj,m,n,visited)){
                dp[p.ii-1][p.jj]=grid[p.ii-1][p.jj]+dp[p.ii][p.jj];
                q.add(new pair(p.ii-1,p.jj,dp[p.ii-1][p.jj]));
                visited[p.ii-1][p.jj]=true;
            }
            if(valid(p.ii+1,p.jj,m,n,visited)){
                dp[p.ii+1][p.jj]=grid[p.ii+1][p.jj]+dp[p.ii][p.jj];
                q.add(new pair(p.ii+1,p.jj,dp[p.ii+1][p.jj]));
